  const [posts, setPosts] = useState([]);

  const location = useLocation();
  console.log(location);
  const query = useParams();
  console.log(query);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await axios.get("/posts");
        setPosts(res.data);
      } catch (err) {
        console.log(err);
      }
    };
    fetchData();
  }, []);
