import React, { useEffect, useState } from "react";
import Edit from "../img/edit.png";
import Delete from "../img/delete.png";
import { Link, useParams } from "react-router-dom";
import Menu from "../components/Menu";
import axios from "axios";

const Single = () => {
  const [post, setPost] = useState([]);

  const { id: postId } = useParams();
  console.log(postId);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await axios.get(`/posts/${postId}`);
        setPost(res.data);
      } catch (err) {
        console.log(err);
      }
    };
    fetchData();
  }, [postId]);

  return (
    <div className="single">
      <div className="content">
        <img src="" alt="" />
        <div className="user">
          <img src="" alt="" />
          <div className="info">
            <span>John</span>
            <p>Posted 2 days ago</p>
          </div>
          <div className="edit">
            <Link to={`/write?edit=2`}>
              <img src={Edit} alt="edit" />
            </Link>
            <img src={Delete} alt="del" />
          </div>
        </div>
        <h1>
          Lorem ipsum dolor sit amet consectetur adipisicing elit. Facere
          exercitationem a culpa iure similique, praesentium dolorem vitae dolor
          quod ipsum quisquam unde doloribus minima maiores! Nisi quasi
          voluptatem fuga libero?
        </h1>
        <p>
          Lorem ipsum dolor sit amet consectetur, adipisicing elit. Hic repellat
          voluptas dolore autem fugit distinctio dolor magni commodi quibusdam,
          eos, magnam asperiores saepe officiis ipsam porro ad aliquid quidem
          officia! Lorem ipsum dolor sit amet consectetur adipisicing elit.
          Tenetur quasi ipsam autem magnam mollitia non quisquam error
          consequuntur veniam quibusdam. Ea impedit culpa alias iure natus saepe
          aperiam atque vel!
        </p>
      </div>
      <Menu />
    </div>
  );
};

export default Single;
